import type { RequestHeaders } from "@octokit/types/dist-types/RequestHeaders.js";
import type { RequestMethod } from "@octokit/types/dist-types/RequestMethod.js";
import type { RequestParameters } from "@octokit/types/dist-types/RequestParameters.js";
import type { Url } from "@octokit/types/dist-types/Url.js";
/**
 * The `.endpoint()` method is guaranteed to set all keys defined by RequestParameters
 * as well as the method property.
 */
export type EndpointDefaults = RequestParameters & {
    baseUrl: Url;
    method: RequestMethod;
    url?: Url;
    headers: RequestHeaders & {
        accept: string;
        "user-agent": string;
    };
    mediaType: {
        format: string;
        previews?: string[];
    };
};
